import React, {useState, useEffect} from "react";
import styles from "./Ghana.module.css";
import { Card, Typography, Grid } from "@material-ui/core";
import CountUp from "react-countup";
import cx from "classnames";
import { fetchData } from "..../api";

function Ghana() {
    const [info, setInfo] = useState({country:"Ghana"});

    useEffect(()=>{
        async function data(){
            const results = await fetchData(info.country);
            setInfo(res=>({...res, results}))
        }
        data()
    },[info])

    const { confirmed, deaths, recovered, lastUpdate } = info;


  return (
    <div className="container">
      Info for Ghana
      <Grid container justify="center" spacing={3}>
        <Grid
          item
          xs={12}
          md={3}
          component={Card}
          className={cx(styles.cards, styles.infected, styles.test1)}
        >
          <Typography gutterBottom color="textSecondary">
            Infected
          </Typography>
          <Typography variant="h5">
            <CountUp
              start={0}
              end={confirmed.value}
              separator=","
              duration={2}
            />
          </Typography>
          <Typography color="textSecondary">
            {new Date(lastUpdate).toDateString()}
          </Typography>
          <Typography variant="body2">Number of Active cases</Typography>
        </Grid>
        <Grid
          item
          xs={12}
          md={3}
          component={Card}
          className={cx(styles.cards, styles.recovered, styles.test1)}
        >
          <Typography gutterBottom color="textSecondary">
            Recovered
          </Typography>
          <Typography variant="h5">
            <CountUp
              start={0}
              end={recovered.value}
              separator=","
              duration={2}
            />
          </Typography>
          <Typography color="textSecondary">
            {new Date(lastUpdate).toDateString()}
          </Typography>
          <Typography variant="body2">Number of Recovered cases</Typography>
        </Grid>
        <Grid
          item
          xs={12}
          md={3}
          component={Card}
          className={cx(styles.cards, styles.deaths, styles.test1)}
        >
          <Typography gutterBottom color="textSecondary">
            Deaths
          </Typography>
          <Typography variant="h5">
            <CountUp start={0} end={deaths.value} separator="," duration={2} />
          </Typography>
          <Typography color="textSecondary">
            {new Date(lastUpdate).toDateString()}
          </Typography>
          <Typography variant="body2">Number of Death cases</Typography>
        </Grid>
      </Grid>

    </div>
  );
}

export default Ghana;
